// ==== 2) Resolver path gambar (aman untuk berbagai lokasi) ====
private java.io.File resolveImageFile(String imagePath) {
    if (imagePath == null || imagePath.isBlank()) return null;

    // coba path apa adanya
    java.io.File f = new java.io.File(imagePath);
    if (f.exists()) return f;

    // buang leading slash jika ada
    String p = imagePath.startsWith("/") ? imagePath.substring(1) : imagePath;

    // coba beberapa lokasi umum projek NetBeans
    String[] tries = {
        p,
        "src/" + p,
        "src/View/" + p,
        "View/" + p,
        "build/classes/" + p
    };
    for (String t : tries) {
        f = new java.io.File(t);
        if (f.exists()) return f;
    }
    return null; // tidak ketemu
}

