    private void buildDynamicBoluCards() {
     // 1. Ambil data BOLU dari database
    java.util.List<Product> items =
            productController.ambilByKategori("BOLU"); // <-- SESUAIKAN string kategori dgn InputMenu (BOLU/Bolu/bolu)

    if (items == null) {
        items = java.util.Collections.emptyList();
    }

    // 2. Bersihkan panel utama Bolu
    jPanel38.removeAll();
    jPanel38.setBackground(new java.awt.Color(209, 186, 155));
    jPanel38.setLayout(new java.awt.BorderLayout());

    // 3. Header judul "Bolu" di atas, di tengah
    javax.swing.JPanel header = new javax.swing.JPanel();
    header.setOpaque(false);
    header.setLayout(new java.awt.FlowLayout(java.awt.FlowLayout.CENTER, 0, 25));
    jLabel68.setHorizontalAlignment(javax.swing.SwingConstants.CENTER);
    header.add(jLabel68);
    jPanel38.add(header, java.awt.BorderLayout.NORTH);

    // 4. Grid 4 kolom untuk kartu bolu
    javax.swing.JPanel grid = new javax.swing.JPanel(
            new java.awt.GridLayout(0, 4, 40, 25) // 4 kolom, jarak antar kartu
    );
    grid.setOpaque(false);

    for (Product p : items) {
        grid.add(buatCardBolu(p));
    }

    // 5. ScrollPane supaya kalau menu bolu banyak bisa discroll
    javax.swing.JScrollPane scroll = new javax.swing.JScrollPane(grid);
    scroll.setBorder(null);
    scroll.setHorizontalScrollBarPolicy(
            javax.swing.ScrollPaneConstants.HORIZONTAL_SCROLLBAR_NEVER);
    scroll.getVerticalScrollBar().setUnitIncrement(16);
    scroll.setOpaque(false);
    scroll.getViewport().setOpaque(false);

    jPanel38.add(scroll, java.awt.BorderLayout.CENTER);

    jPanel38.revalidate();
    jPanel38.repaint();
}
// 1 kartu bolu (mirip desain sekarang)
private javax.swing.JPanel buatCardBolu(Product p) {
    javax.swing.JPanel card = new javax.swing.JPanel();
    card.setBackground(java.awt.Color.WHITE);
    card.setLayout(new javax.swing.BoxLayout(card, javax.swing.BoxLayout.Y_AXIS));
    // padding atas dikurangi biar konten sedikit lebih ke atas
    card.setBorder(javax.swing.BorderFactory.createEmptyBorder(10, 14, 12, 14));

    card.setPreferredSize(new java.awt.Dimension(210, 280));
    card.setMaximumSize(new java.awt.Dimension(210, 280));
    card.setAlignmentX(java.awt.Component.CENTER_ALIGNMENT);

    // ===== Gambar =====
    javax.swing.JLabel img = new javax.swing.JLabel();
    img.setAlignmentX(java.awt.Component.CENTER_ALIGNMENT);
    img.setHorizontalAlignment(javax.swing.SwingConstants.CENTER);

    int targetW = 170;
    int targetH = 140;

    java.io.File f = resolveImageFile(p.getImagePath());
    if (f != null) {
        try {
            java.awt.image.BufferedImage bi = javax.imageio.ImageIO.read(f);
            if (bi != null) {
                java.awt.Image scaled =
                        bi.getScaledInstance(targetW, targetH, java.awt.Image.SCALE_SMOOTH);
                img.setIcon(new javax.swing.ImageIcon(scaled));
            } else {
                img.setText("No Image");
            }
        } catch (Exception e) {
            img.setText("No Image");
        }
    } else {
        img.setText("No Image");
    }

    // ===== NAMA (RATA KIRI) =====
    javax.swing.JLabel nama = new javax.swing.JLabel(p.getName());
    nama.setFont(new java.awt.Font("Segoe UI", java.awt.Font.BOLD, 14));
    nama.setAlignmentX(java.awt.Component.LEFT_ALIGNMENT);
    nama.setHorizontalAlignment(javax.swing.SwingConstants.LEFT);

    // ===== HARGA (RATA KIRI) =====
    javax.swing.JLabel harga = new javax.swing.JLabel("Rp. " + formatRupiah(p.getPrice()));
    harga.setFont(new java.awt.Font("Segoe UI", java.awt.Font.PLAIN, 14));
    harga.setForeground(new java.awt.Color(158, 115, 52));
    harga.setAlignmentX(java.awt.Component.LEFT_ALIGNMENT);
    harga.setHorizontalAlignment(javax.swing.SwingConstants.LEFT);

    // ===== ROW JUMLAH + SPINNER (RATA KIRI) =====
    javax.swing.JPanel qtyRow = new javax.swing.JPanel(
            new java.awt.FlowLayout(java.awt.FlowLayout.LEFT, 5, 0)
    );
    qtyRow.setOpaque(false);
    qtyRow.setAlignmentX(java.awt.Component.LEFT_ALIGNMENT);

    javax.swing.JLabel lQty = new javax.swing.JLabel("Jumlah");
    javax.swing.JSpinner sp = new javax.swing.JSpinner(
            new javax.swing.SpinnerNumberModel(0, 0, 999, 1)
    );
    qtyRow.add(lQty);
    qtyRow.add(sp);

    // ===== TOMBOL =====
    javax.swing.JButton btn = new javax.swing.JButton("Tambahkan Pesanan");
    btn.setBackground(new java.awt.Color(130, 87, 87));
    btn.setForeground(java.awt.Color.WHITE);
    btn.setFont(new java.awt.Font("Segoe UI", java.awt.Font.BOLD, 12));
    btn.setAlignmentX(java.awt.Component.CENTER_ALIGNMENT);

    btn.addActionListener(e -> {
        int jumlah = (Integer) sp.getValue();
        if (jumlah <= 0) {
            javax.swing.JOptionPane.showMessageDialog(this, "Jumlah harus > 0");
            return;
        }
        KeranjangController.getInstance().tambahItem(p, jumlah);
        javax.swing.JOptionPane.showMessageDialog(this, "Ditambahkan ke keranjang");
        sp.setValue(0);
    });

    // ===== SUSUN DI DALAM CARD =====
    card.add(img);
    card.add(javax.swing.Box.createVerticalStrut(6));   // jarak kecil ke nama
    card.add(nama);
    card.add(javax.swing.Box.createVerticalStrut(2));
    card.add(harga);
    card.add(javax.swing.Box.createVerticalStrut(6));
    card.add(qtyRow);
    card.add(javax.swing.Box.createVerticalStrut(8));
    card.add(btn);

    return card;
}

// helper: cari file gambar dari path relatif
private java.io.File resolveImageFile(String imagePath) {
    if (imagePath == null || imagePath.isBlank()) return null;

    java.io.File f = new java.io.File(imagePath);
    if (f.exists()) return f;

    String p = imagePath.startsWith("/") ? imagePath.substring(1) : imagePath;

    f = new java.io.File("src/" + p);
    if (f.exists()) return f;

    f = new java.io.File("src/View/" + p);
    if (f.exists()) return f;

    f = new java.io.File("build/classes/" + p);
    if (f.exists()) return f;

    return null;
}
    private static String formatRupiah(double v) {
    java.text.DecimalFormat df = new java.text.DecimalFormat("#,###");
    return df.format(v).replace(",", ".");
}
