    // ============================
    // MEMBANGUN PANEL DINAMIS PRODUK
    // ============================
    private void setupDynamicLayout() {
        List<Model.Product> items = productController.ambilByKategori("BOLU");

        pnlDynamic = new JPanel(new BorderLayout());
        Color coklat = new Color(209, 186, 155);
        pnlDynamic.setBackground(coklat);

        JPanel grid = new JPanel(new GridLayout(0, 4, 25, 25));
        grid.setOpaque(false);

        for (Model.Product p : items) {
            grid.add(buatCardProduk(p));
        }

        JPanel wrapper = new JPanel(new BorderLayout());
        wrapper.setOpaque(false);
        wrapper.setBorder(BorderFactory.createEmptyBorder(20, 60, 20, 60));
        wrapper.add(grid, BorderLayout.CENTER);

        JScrollPane scroll = new JScrollPane(wrapper);
        scroll.setBorder(null);
        scroll.setHorizontalScrollBarPolicy(ScrollPaneConstants.HORIZONTAL_SCROLLBAR_NEVER);
        scroll.setVerticalScrollBarPolicy(ScrollPaneConstants.VERTICAL_SCROLLBAR_AS_NEEDED);
        scroll.getVerticalScrollBar().setUnitIncrement(16);
        scroll.getViewport().setBackground(coklat);
        scroll.setBackground(coklat);

        pnlDynamic.add(scroll, BorderLayout.CENTER);
    }

    // ============================
    // FUNGSI MEMBUAT CARD PRODUK
    // ============================
    private JPanel buatCardProduk(Model.Product p) {
        JPanel card = new JPanel();
        card.setLayout(new BoxLayout(card, BoxLayout.Y_AXIS));
        card.setBackground(Color.WHITE);
        card.setBorder(BorderFactory.createEmptyBorder(10, 10, 10, 10));

        JLabel img = new JLabel(new ImageIcon(getClass().getResource(p.getGambarPath())));
        img.setAlignmentX(Component.CENTER_ALIGNMENT);

        JLabel nama = new JLabel(p.getNama());
        nama.setFont(new Font("Segoe UI", Font.BOLD, 14));
        nama.setAlignmentX(Component.CENTER_ALIGNMENT);

        JLabel harga = new JLabel(String.format("Rp %, .0f", p.getHarga()));
        harga.setForeground(new Color(158, 115, 52));
        harga.setAlignmentX(Component.CENTER_ALIGNMENT);

        JPanel qtyPanel = new JPanel();
        qtyPanel.setOpaque(false);
        JLabel lblJumlah = new JLabel("Jumlah: ");
        JSpinner spinner = new JSpinner(new SpinnerNumberModel(0, 0, 100, 1));
        qtyPanel.add(lblJumlah);
        qtyPanel.add(spinner);

        JButton btnAdd = new JButton("Tambahkan Pesanan");
        btnAdd.setBackground(new Color(130, 87, 87));
        btnAdd.setForeground(Color.WHITE);
        btnAdd.setAlignmentX(Component.CENTER_ALIGNMENT);

        btnAdd.addActionListener(e -> {
            int jumlah = (int) spinner.getValue();
            if (jumlah <= 0) {
                JOptionPane.showMessageDialog(this, "Jumlah harus lebih dari 0!");
                return;
            }
            Controller.KeranjangController.getInstance().tambahItem(p, jumlah);
            JOptionPane.showMessageDialog(this, "Produk ditambahkan ke keranjang!");
            spinner.setValue(0);
        });

        card.add(img);
        card.add(Box.createVerticalStrut(10));
        card.add(nama);
        card.add(harga);
        card.add(Box.createVerticalStrut(5));
        card.add(qtyPanel);
        card.add(Box.createVerticalStrut(5));
        card.add(btnAdd);

        return card;
    }

